import {useState} from 'react';
import Navbar from './Navbar';
import Home from './Component/Home';
import Footer from './Footer';
import Resume from './Component/Resume';

export const Main = () => {
    const [value, setValue] = useState();    

    // const calculate  = () => {
    //     if(value == 'project') {
    //         return <Footer/>
    //     }
    // }
    // return (
    //     <>
    //     <Navbar setValue={setValue}/>
    //     {/* {
    //         calculate()
    //     } */}
    //     {value}

    //     <Footer />
    //     </>
    // )
    if(value=='project'){
        return(
            <>
                <Navbar setValue={setValue} />
                <Footer />
            </>
        );
    }
    else if(value == 'home'){
        return(
            <>
                <Navbar setValue={setValue} />
                <Home />
                <Footer />
            </>
        );
    }
    else if(value == 'home'){
        return(
            <>
                <Navbar setValue={setValue} />
                <Resume />
                <Footer />
            </>
        );
    }
    else{
        return(
            <Navbar setValue={setValue} />
        );
    }
}
